import React from 'react';
import logo from './logo.svg';
import './App.css';
import Carta from './Carta';

import Fab from '@material-ui/core/Fab';
import ArrowForwardIcon from '@material-ui/icons/ArrowForward';
import CheckIcon from '@material-ui/icons/ArrowForward';

import { makeStyles, useTheme } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  root: {
    backgroundColor: theme.palette.background.paper,
    width: 500,
    position: 'relative',
    minHeight: 200,
  },
  fab: {
    position: 'absolute',
    bottom: theme.spacing(2),
    right: theme.spacing(2),
  },
}));

const palabras = [
  {
    palabra: 'Sueño',
    tabues: [
        'Dormir',
        'Cama',
        'Noche',
        'Soñar',
        'Roncar'
    ]
  },
  {
    palabra: 'Futbol',
    tabues: [
      'Pelota',
      'Deporte',
      'Maradona',
      'Cancha',
      'Messi'
    ]
  },
  {
    palabra: 'Pictionary',
    tabues: [
      'Juego',
      'Dibujar',
      'Lapiz',
      'Tablero',
      'Marca'
    ]
  },
  {
    palabra: 'Guardapolvo',
    tabues: [
      'Blanco',
      'Escuela',
      'Primaria',
      'Maestra',
      'Alumno'
    ]
  },
  {
    palabra: 'Palmera',
    tabues: [
      'Datil',
      'Coco',
      'Arbol',
      'Mono',
      'Trópico'
    ]
  },
  {
    palabra: 'Navidad',
    tabues: [
      'Diciembre',
      'Papa Noel',
      'Villancicos',
      'Arbolito',
      'Nacimiento'
    ]
  },
  {
    palabra: 'Comer',
    tabues: [
      'Comida',
      'Boca',
      'Hambre',
      'Heladera'
    ]
  }

];

function App() {
  const classes = useStyles();
  const [indexCarta, setIndexCarta] = React.useState(0);

  return (
    <div className="App">
      <Carta carta={palabras[indexCarta]}/>
      <Fab className={classes.fab} onClick={() => { setIndexCarta(Math.floor(Math.random() * palabras.length)) }}>
          <ArrowForwardIcon />
      </Fab>
    </div>
  );
}

export default App;
